package com.example.challengeyape.model.provider

import com.example.challengeyape.model.CoordinatesModel
import com.example.challengeyape.model.RecipeModel
import io.mockk.*
import io.mockk.impl.annotations.InjectMockKs
import io.mockk.impl.annotations.MockK
import io.mockk.impl.annotations.RelaxedMockK
import kotlinx.coroutines.runBlocking
import org.junit.Before
import org.junit.Test

class SearchRecipeTest {

    @RelaxedMockK
    private lateinit var listRecipe: List<RecipeModel>

    @RelaxedMockK
    private lateinit var query: String

    lateinit var searchRecipe : SearchRecipe

    @Before
    fun onBefore(){
        MockKAnnotations.init(this)
        query = "ceviche"
        searchRecipe = SearchRecipe(query)
    }

    @Test
    fun whenSearchRecipeIsNotEmpty()= runBlocking {
        //Given
        val myList = listOf(
            RecipeModel(
                "Ceviche",
                "Pollo hecho a la brasa a compa√±ado de papas fritas",
                "data:image/jpeg;base64,",
                CoordinatesModel(
                    "-12.205349",
                    "-77.012223"
                ), listOf()
            )
        )
        every { listRecipe } returns myList

        val response = searchRecipe.onSearchRecipe()

        verify { assert(response == myList) }
    }
}